import React, { useState, useEffect, createContext, useContext } from 'react';
import {
  Sun,
  Moon,
  Settings,
  X,
  Check
} from 'lucide-react';

// Theme Context
const ThemeContext = createContext();

// Custom Font Sizes
const fontSizes = {
  small: {
    base: '0.875rem',
    heading: '1.5rem'
  },
  medium: {
    base: '1rem',
    heading: '1.875rem'
  },
  large: {
    base: '1.125rem',
    heading: '2.25rem'
  }
};

// Customization Modal
const CustomizationModal = ({ isOpen, onClose }) => {
  const {
    currentTheme,
    setCurrentTheme,
    fontSize,
    setFontSize,
    spacing,
    setSpacing,
    themes
  } = useContext(ThemeContext);
  
  const [localTheme, setLocalTheme] = useState(currentTheme);
  const [localFontSize, setLocalFontSize] = useState(fontSize);
  const [localSpacing, setLocalSpacing] = useState(spacing);

  const saveChanges = () => {
    setCurrentTheme(localTheme);
    setFontSize(localFontSize);
    setSpacing(localSpacing);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div 
        className="rounded-lg p-6 max-w-md w-full max-h-[80vh] overflow-y-auto"
        style={{ 
          backgroundColor: themes[currentTheme].colors.background,
          color: themes[currentTheme].colors.text 
        }}
      >
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold">Customize Theme</h2>
          <button onClick={onClose}>
            <X className="w-6 h-6" />
          </button>
        </div>

        <div className="space-y-6">
          <div>
            <h3 className="font-semibold mb-2">Font Size</h3>
            <div className="flex gap-2">
              {['small', 'medium', 'large'].map(size => (
                <button
                  key={size}
                  onClick={() => setLocalFontSize(size)}
                  className="px-3 py-1 rounded-full"
                  style={{
                    backgroundColor: localFontSize === size ? themes[currentTheme].colors.primary : themes[currentTheme].colors.secondary,
                    color: localFontSize === size ? '#fff' : themes[currentTheme].colors.text
                  }}
                >
                  {size}
                </button>
              ))}
            </div>
          </div>

          <div>
            <h3 className="font-semibold mb-2">Spacing</h3>
            <input
              type="range"
              min="1"
              max="8"
              value={localSpacing}
              onChange={(e) => setLocalSpacing(Number(e.target.value))}
              className="w-full"
            />
          </div>

          <div className="flex gap-2 justify-end">
            <button
              onClick={onClose}
              className="px-4 py-2 rounded"
              style={{ backgroundColor: themes[currentTheme].colors.secondary }}
            >
              Cancel
            </button>
            <button
              onClick={saveChanges}
              className="px-4 py-2 rounded text-white"
              style={{ backgroundColor: themes[currentTheme].colors.primary }}
            >
              Save Changes
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Theme Grid Component
const ThemeGrid = () => {
  const { currentTheme, setCurrentTheme, themes } = useContext(ThemeContext);
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  const filteredThemes = Object.entries(themes).filter(([key, theme]) => {
    const matchesFilter = filter === 'all' || key.startsWith(filter);
    const matchesSearch = theme.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         key.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesFilter && matchesSearch;
  });

  return (
    <div className="p-4 space-y-4">
      <div className="flex flex-col md:flex-row gap-4 justify-between items-start md:items-center">
        <h2 className="text-lg font-bold">Choose Your Theme</h2>
        
        <div className="flex flex-col sm:flex-row gap-4 w-full md:w-auto">
          {/* Search Input */}
          <input
            type="text"
            placeholder="Search themes..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="px-3 py-1 rounded-lg w-full sm:w-64"
            style={{
              backgroundColor: themes[currentTheme].colors.secondary,
              color: themes[currentTheme].colors.text,
              border: `1px solid ${themes[currentTheme].colors.accent}`
            }}
          />
          
          {/* Filter Buttons */}
          <div className="flex gap-2 flex-wrap">
            {['all', 'light', 'dark', 'special'].map((type) => (
              <button
                key={type}
                onClick={() => setFilter(type)}
                className="px-3 py-1 rounded-full transition-all duration-300"
                style={{
                  backgroundColor: filter === type ? themes[currentTheme].colors.primary : themes[currentTheme].colors.secondary,
                  color: filter === type ? '#fff' : themes[currentTheme].colors.text
                }}
              >
                {type.charAt(0).toUpperCase() + type.slice(1)}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Scrollable Theme Grid */}
      <div 
        className="overflow-y-auto max-h-[70vh] rounded-lg p-4" 
        style={{
          backgroundColor: themes[currentTheme].colors.secondary,
          scrollbarWidth: 'thin',
          scrollbarColor: `${themes[currentTheme].colors.primary} ${themes[currentTheme].colors.secondary}`
        }}
      >
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {filteredThemes.map(([key, theme]) => (
            <div
              key={key}
              onClick={() => setCurrentTheme(key)}
              className={`p-4 rounded-lg cursor-pointer transition-all duration-300 hover:scale-105 ${
                currentTheme === key ? 'ring-2 transform scale-105' : ''
              }`}
              style={{
                backgroundColor: theme.colors.background,
                color: theme.colors.text,
                borderColor: theme.colors.primary,
                boxShadow: currentTheme === key ? `0 0 0 2px ${theme.colors.accent}` : 'none'
              }}
            >
              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <h3 className="font-semibold truncate">
                    {theme.name}
                  </h3>
                  {currentTheme === key && (
                    <Check className="w-4 h-4" style={{ color: theme.colors.accent }} />
                  )}
                </div>

                {/* Color Preview Grid */}
                <div className="grid grid-cols-5 gap-1">
                  {Object.entries(theme.colors).map(([colorName, colorValue]) => (
                    <div
                      key={colorName}
                      className="relative group"
                      title={`${colorName}: ${colorValue}`}
                    >
                      <div
                        className="w-full h-6 rounded transition-transform group-hover:scale-110"
                        style={{ backgroundColor: colorValue }}
                      />
                      <span className="absolute bottom-full left-1/2 transform -translate-x-1/2 
                        bg-black text-white px-2 py-1 rounded text-xs opacity-0 group-hover:opacity-100 
                        transition-opacity whitespace-nowrap pointer-events-none z-10">
                        {colorName}
                      </span>
                    </div>
                  ))}
                </div>

                <div className="flex justify-between items-center text-sm opacity-75">
                  <span>{key.replace(/([0-9]+)/, ' $1')}</span>
                  <span style={{ color: theme.colors.accent }}>
                    {theme.colors.primary.toUpperCase()}
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

// Header Component
const Header = () => {
  const { isDark, toggleDark, currentTheme, themes } = useContext(ThemeContext);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  
  return (
    <header className="p-4 flex justify-between items-center" style={{
      backgroundColor: themes[currentTheme].colors.secondary,
      color: themes[currentTheme].colors.text
    }}>
      <h1 className="text-xl font-bold">Theme Switcher</h1>
      <div className="flex gap-2">
        <button
          onClick={() => setIsSettingsOpen(true)}
          className="p-2 rounded-full hover:bg-opacity-80 transition-all duration-300"
          style={{ backgroundColor: themes[currentTheme].colors.primary }}
        >
          <Settings className="w-6 h-6 text-white" />
        </button>
        <button
          onClick={toggleDark}
          className="p-2 rounded-full hover:bg-opacity-80 transition-all duration-300"
          style={{ backgroundColor: themes[currentTheme].colors.primary }}
        >
          {isDark ? <Sun className="w-6 h-6 text-white" /> : <Moon className="w-6 h-6 text-white" />}
        </button>
      </div>
      <CustomizationModal isOpen={isSettingsOpen} onClose={() => setIsSettingsOpen(false)} />
    </header>
  );
};

// Main App Component
const App = () => {
  const [themes, setThemes] = useState({});
  const [currentTheme, setCurrentTheme] = useState('light1');
  const [fontSize, setFontSize] = useState('medium');
  const [spacing, setSpacing] = useState(4);
  const isDark = currentTheme.startsWith('dark');

  const toggleDark = () => {
    const themeKeys = Object.keys(themes);
    const currentIndex = themeKeys.indexOf(currentTheme);
    const totalThemes = themeKeys.length;
    const halfWay = Math.floor(totalThemes / 2);
    
    if (isDark) {
      setCurrentTheme(themeKeys[currentIndex % halfWay]);
    } else {
      setCurrentTheme(themeKeys[halfWay + (currentIndex % halfWay)]);
    }
  };

  // Load themes from JSON
  useEffect(() => {
    fetch('/themes.json')
      .then(response => response.json())
      .then(data => {
        setThemes(data);
        const savedTheme = localStorage.getItem('theme');
        if (savedTheme && data[savedTheme]) {
          setCurrentTheme(savedTheme);
        }
        
        const savedFontSize = localStorage.getItem('fontSize');
        const savedSpacing = localStorage.getItem('spacing');
        if (savedFontSize) setFontSize(savedFontSize);
        if (savedSpacing) setSpacing(Number(savedSpacing));
      })
      .catch(error => {
        console.error('Error loading themes:', error);
      });
  }, []);

  // Save preferences to localStorage
  useEffect(() => {
    if (Object.keys(themes).length === 0) return;
    localStorage.setItem('theme', currentTheme);
    localStorage.setItem('fontSize', fontSize);
    localStorage.setItem('spacing', spacing.toString());
  }, [currentTheme, fontSize, spacing, themes]);

  if (Object.keys(themes).length === 0) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <p>Loading themes...</p>
      </div>
    );
  }

  return (
    <ThemeContext.Provider value={{ 
      currentTheme, 
      setCurrentTheme, 
      isDark, 
      toggleDark,
      fontSize,
      setFontSize,
      spacing,
      setSpacing,
      themes
    }}>
      <div
        className="min-h-screen transition-all duration-300"
        style={{
          backgroundColor: themes[currentTheme].colors.background,
          color: themes[currentTheme].colors.text,
          fontSize: fontSizes[fontSize].base,
          padding: `${spacing * 0.25}rem`
        }}
      >
        <Header />
        <main className="container mx-auto">
          <ThemeGrid />
          
          <div className="mt-8 grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="p-4 rounded-lg" style={{ backgroundColor: themes[currentTheme].colors.secondary }}>
              <h2 className="text-lg font-bold mb-4">Current Theme Preview</h2>
              <div className="space-y-4">
                <div className="flex gap-2">
                  <button
                    className="px-4 py-2 rounded text-white transition-all duration-300 hover:scale-105"
                    style={{ backgroundColor: themes[currentTheme].colors.primary }}
                  >
                    Primary Button
                  </button>
                  <button
                    className="px-4 py-2 rounded transition-all duration-300 hover:scale-105"
                    style={{ backgroundColor: themes[currentTheme].colors.secondary }}
                  >
                    Secondary Button
                  </button>
                </div>
                <div className="p-4 rounded" style={{ backgroundColor: themes[currentTheme].colors.accent }}>
                  <p className="text-white">Accent Background</p>
                </div>
                <div className="space-y-2">
                  <h3 className="font-bold">Typography</h3>
                  <p>Regular text with current theme colors</p>
                  <p className="opacity-75">Secondary text example</p>
                </div>
              </div>
            </div>

            <div className="p-4 rounded-lg" style={{ backgroundColor: themes[currentTheme].colors.secondary }}>
              <h2 className="text-lg font-bold mb-4">Theme Information</h2>
              <div className="space-y-2">
                {Object.entries(themes[currentTheme].colors).map(([name, color]) => (
                  <div key={name} className="flex items-center gap-2">
                    <div
                      className="w-6 h-6 rounded"
                      style={{ backgroundColor: color }}
                    />
                    <span className="capitalize">{name}: {color}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
</main>

        <footer 
          className="mt-8 p-4 text-center rounded-lg"
          style={{ 
            backgroundColor: themes[currentTheme].colors.secondary,
            fontSize: fontSizes[fontSize].base
          }}
        >
          <div className="container mx-auto">
            <p className="mb-2">Theme Switcher Demo</p>
            <div className="flex justify-center gap-4 text-sm opacity-75">
              <div>Current Theme: <span className="font-semibold">{themes[currentTheme].name}</span></div>
              <div>Font Size: <span className="font-semibold">{fontSize}</span></div>
              <div>Spacing: <span className="font-semibold">{spacing}</span></div>
            </div>
          </div>
        </footer>

        {/* Floating Theme Indicator */}
        <div 
          className="fixed bottom-4 right-4 px-4 py-2 rounded-full text-sm transition-all duration-300"
          style={{ 
            backgroundColor: themes[currentTheme].colors.primary,
            color: '#ffffff',
            boxShadow: `0 2px 8px ${themes[currentTheme].colors.primary}80`
          }}
        >
          {themes[currentTheme].name}
        </div>
      </div>
    </ThemeContext.Provider>
  );
};

export default App;
