import React, { useState, useEffect, createContext, useContext } from 'react';
import {
  Sun,
  Moon,
  ChevronLeft,
  ChevronRight,
  Settings,
  Palette,
  Sliders,
  X
} from 'lucide-react';

// Theme Context
const ThemeContext = createContext();

// Custom Font Sizes
const fontSizes = {
  small: {
    base: '0.875rem',
    heading: '1.5rem'
  },
  medium: {
    base: '1rem',
    heading: '1.875rem'
  },
  large: {
    base: '1.125rem',
    heading: '2.25rem'
  }
};

// Customization Modal
const CustomizationModal = ({ isOpen, onClose }) => {
  const {
    currentTheme,
    setCurrentTheme,
    fontSize,
    setFontSize,
    spacing,
    setSpacing,
    themes
  } = useContext(ThemeContext);
  
  const [localTheme, setLocalTheme] = useState(currentTheme);
  const [localFontSize, setLocalFontSize] = useState(fontSize);
  const [localSpacing, setLocalSpacing] = useState(spacing);

  const saveChanges = () => {
    setCurrentTheme(localTheme);
    setFontSize(localFontSize);
    setSpacing(localSpacing);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div 
        className="rounded-lg p-6 max-w-md w-full max-h-[80vh] overflow-y-auto"
        style={{ 
          backgroundColor: themes[currentTheme].colors.background,
          color: themes[currentTheme].colors.text 
        }}
      >
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold">Customize Theme</h2>
          <button onClick={onClose}>
            <X className="w-6 h-6" />
          </button>
        </div>

        <div className="space-y-6">
          <div>
            <h3 className="font-semibold mb-2">Font Size</h3>
            <div className="flex gap-2">
              {['small', 'medium', 'large'].map(size => (
                <button
                  key={size}
                  onClick={() => setLocalFontSize(size)}
                  className="px-3 py-1 rounded-full"
                  style={{
                    backgroundColor: localFontSize === size ? themes[currentTheme].colors.primary : themes[currentTheme].colors.secondary,
                    color: localFontSize === size ? '#fff' : themes[currentTheme].colors.text
                  }}
                >
                  {size}
                </button>
              ))}
            </div>
          </div>

          <div>
            <h3 className="font-semibold mb-2">Spacing</h3>
            <input
              type="range"
              min="1"
              max="8"
              value={localSpacing}
              onChange={(e) => setLocalSpacing(Number(e.target.value))}
              className="w-full"
            />
          </div>

          <div className="flex gap-2 justify-end">
            <button
              onClick={onClose}
              className="px-4 py-2 rounded"
              style={{ backgroundColor: themes[currentTheme].colors.secondary }}
            >
              Cancel
            </button>
            <button
              onClick={saveChanges}
              className="px-4 py-2 rounded text-white"
              style={{ backgroundColor: themes[currentTheme].colors.primary }}
            >
              Save Changes
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Interactive Card Component
const InteractiveCard = ({ title, children }) => {
  const { currentTheme, themes } = useContext(ThemeContext);
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className="p-4 rounded-lg transition-all duration-300 transform"
      style={{
        backgroundColor: themes[currentTheme].colors.secondary,
        transform: isHovered ? 'scale(1.02)' : 'scale(1)',
        boxShadow: isHovered ? `0 8px 24px ${themes[currentTheme].colors.primary}33` : 'none'
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <h3 className="font-bold mb-2">{title}</h3>
      {children}
    </div>
  );
};

// Header Component with Settings
const Header = () => {
  const { isDark, toggleDark, currentTheme, themes } = useContext(ThemeContext);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  
  return (
    <header className="p-4 flex justify-between items-center" style={{
      backgroundColor: themes[currentTheme].colors.secondary,
      color: themes[currentTheme].colors.text
    }}>
      <h1 className="text-xl font-bold">Theme Switcher</h1>
      <div className="flex gap-2">
        <button
          onClick={() => setIsSettingsOpen(true)}
          className="p-2 rounded-full hover:bg-opacity-80"
          style={{ backgroundColor: themes[currentTheme].colors.primary }}
        >
          <Settings className="w-6 h-6 text-white" />
        </button>
        <button
          onClick={toggleDark}
          className="p-2 rounded-full hover:bg-opacity-80"
          style={{ backgroundColor: themes[currentTheme].colors.primary }}
        >
          {isDark ? <Sun className="w-6 h-6 text-white" /> : <Moon className="w-6 h-6 text-white" />}
        </button>
      </div>
      <CustomizationModal isOpen={isSettingsOpen} onClose={() => setIsSettingsOpen(false)} />
    </header>
  );
};

// Theme Preview Component with Animation
const ThemePreview = ({ theme, isActive, onClick }) => {
  return (
    <div
      onClick={onClick}
      className={`p-4 rounded-lg cursor-pointer transition-all duration-300 ${
        isActive ? 'ring-2 transform scale-105' : 'hover:scale-105'
      }`}
      style={{
        backgroundColor: theme.colors.background,
        color: theme.colors.text,
        border: `2px solid ${theme.colors.primary}`
      }}
    >
      <h3 className="font-bold mb-2">{theme.name}</h3>
      <div className="flex gap-2">
        <div
          className="w-6 h-6 rounded"
          style={{ backgroundColor: theme.colors.primary }}
        />
        <div
          className="w-6 h-6 rounded"
          style={{ backgroundColor: theme.colors.secondary }}
        />
        <div
          className="w-6 h-6 rounded"
          style={{ backgroundColor: theme.colors.accent }}
        />
      </div>
    </div>
  );
};

// Theme Carousel Component
const ThemeCarousel = () => {
  const { currentTheme, setCurrentTheme, themes } = useContext(ThemeContext);
  const [startIndex, setStartIndex] = useState(0);
  const visibleThemes = 3;
  const themeKeys = Object.keys(themes);
  const totalThemes = themeKeys.length;

  const nextThemes = () => {
    setStartIndex(Math.min(startIndex + 1, totalThemes - visibleThemes));
  };

  const prevThemes = () => {
    setStartIndex(Math.max(startIndex - 1, 0));
  };

  return (
    <div className="p-4">
      <h2 className="text-lg font-bold mb-4">Choose a Theme</h2>
      <div className="flex items-center gap-4">
        <button
          onClick={prevThemes}
          disabled={startIndex === 0}
          className="p-2 rounded-full disabled:opacity-50 transition-transform hover:scale-110"
          style={{ backgroundColor: themes[currentTheme].colors.primary }}
        >
          <ChevronLeft className="w-6 h-6 text-white" />
        </button>
        
        <div className="flex gap-4 overflow-hidden">
          {themeKeys.slice(startIndex, startIndex + visibleThemes).map((key) => (
            <ThemePreview
              key={key}
              theme={themes[key]}
              isActive={currentTheme === key}
              onClick={() => setCurrentTheme(key)}
            />
          ))}
        </div>

        <button
          onClick={nextThemes}
          disabled={startIndex >= totalThemes - visibleThemes}
          className="p-2 rounded-full disabled:opacity-50 transition-transform hover:scale-110"
          style={{ backgroundColor: themes[currentTheme].colors.primary }}
        >
          <ChevronRight className="w-6 h-6 text-white" />
        </button>
      </div>
    </div>
  );
};

// Main App Component
const App = () => {
  const [themes, setThemes] = useState({});
  const [currentTheme, setCurrentTheme] = useState('light1');
  const [fontSize, setFontSize] = useState('medium');
  const [spacing, setSpacing] = useState(4);
  const isDark = currentTheme.startsWith('dark');

  const toggleDark = () => {
    const themeKeys = Object.keys(themes);
    const currentIndex = themeKeys.indexOf(currentTheme);
    const totalThemes = themeKeys.length;
    const halfWay = Math.floor(totalThemes / 2);
    
    if (isDark) {
      setCurrentTheme(themeKeys[currentIndex % halfWay]);
    } else {
      setCurrentTheme(themeKeys[halfWay + (currentIndex % halfWay)]);
    }
  };

  // Load themes from JSON
  useEffect(() => {
    fetch('/themes.json')
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setThemes(data);

        // After loading themes, set the default theme
        const savedTheme = localStorage.getItem('theme');
        if (savedTheme && data[savedTheme]) {
          setCurrentTheme(savedTheme);
        } else {
          setCurrentTheme('light1'); // Fallback to 'light1'
        }

        // Load other preferences
        const savedFontSize = localStorage.getItem('fontSize');
        const savedSpacing = localStorage.getItem('spacing');

        if (savedFontSize) setFontSize(savedFontSize);
        if (savedSpacing) setSpacing(Number(savedSpacing));
      })
      .catch(error => {
        console.error('Error loading themes:', error);
        // Optionally set a default theme or notify the user
      });
  }, []);

  // Save preferences to localStorage
  useEffect(() => {
    if (Object.keys(themes).length === 0) return; // Prevent saving before themes are loaded
    localStorage.setItem('theme', currentTheme);
    localStorage.setItem('fontSize', fontSize);
    localStorage.setItem('spacing', spacing.toString());
  }, [currentTheme, fontSize, spacing, themes]);

  // Prevent rendering until themes are loaded
  if (Object.keys(themes).length === 0) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <p>Loading themes...</p>
      </div>
    );
  }

  return (
    <ThemeContext.Provider value={{ 
      currentTheme, 
      setCurrentTheme, 
      isDark, 
      toggleDark,
      fontSize,
      setFontSize,
      spacing,
      setSpacing,
      themes // Provide themes in context
    }}>
      <div
        className="min-h-screen transition-all duration-300"
        style={{
          backgroundColor: themes[currentTheme].colors.background,
          color: themes[currentTheme].colors.text,
          fontSize: fontSizes[fontSize].base,
          padding: `${spacing * 0.25}rem`
        }}
      >
        <Header />
        <main className="container mx-auto">
          <ThemeCarousel />
          
          <div className="mt-8 grid grid-cols-1 md:grid-cols-2 gap-4">
            <InteractiveCard title="Theme Preview">
              <div className="space-y-4">
                <button
                  className="px-4 py-2 rounded text-white transition-transform hover:scale-105"
                  style={{ backgroundColor: themes[currentTheme].colors.primary }}
                >
                  Primary Button
                </button>
                <button
                  className="px-4 py-2 rounded ml-2 transition-transform hover:scale-105"
                  style={{ backgroundColor: themes[currentTheme].colors.secondary }}
                >
                  Secondary Button
                </button>
              </div>
            </InteractiveCard>

            <InteractiveCard title="Color Palette">
              <div className="grid grid-cols-2 gap-2">
                {Object.entries(themes[currentTheme].colors).map(([name, color]) => (
                  <div key={name} className="flex items-center gap-2">
                    <div
                      className="w-6 h-6 rounded"
                      style={{ backgroundColor: color }}
                    />
                    <span className="capitalize">{name}</span>
                  </div>
                ))}
              </div>
            </InteractiveCard>

            <InteractiveCard title="Typography">
              <div className="space-y-4">
                <h1 style={{ fontSize: fontSizes[fontSize].heading }}>Heading 1</h1>
                <h2 style={{ fontSize: `calc(${fontSizes[fontSize].heading} * 0.8)` }}>Heading 2</h2>
                <p>Regular paragraph text with current font size setting.</p>
                <p className="text-sm opacity-75">Small text example</p>
              </div>
            </InteractiveCard>

            <InteractiveCard title="Spacing Preview">
              <div className="space-y-2">
                <div 
                  className="p-4 rounded"
                  style={{ 
                    backgroundColor: themes[currentTheme].colors.accent,
                    marginBottom: `${spacing * 0.25}rem`
                  }}
                >
                  Spacing: {spacing}
                </div>
                <div className="flex gap-2">
                  {[1, 2, 3].map((num) => (
                    <div
                      key={num}
                      className="w-8 h-8 rounded flex items-center justify-center text-white"
                      style={{ 
                        backgroundColor: themes[currentTheme].colors.primary,
                        margin: `${(spacing * 0.25) / 2}rem`
                      }}
                    >
                      {num}
                    </div>
                  ))}
                </div>
              </div>
            </InteractiveCard>
          </div>

          <div className="mt-8">
            <InteractiveCard title="Interactive Features Demo">
              <div className="space-y-4">
                <div className="flex flex-wrap gap-2">
                  {['primary', 'secondary', 'accent'].map((type) => (
                    <button
                      key={type}
                      className="px-4 py-2 rounded text-white transition-all duration-300 hover:scale-105"
                      style={{ 
                        backgroundColor: themes[currentTheme].colors[type],
                        transform: `translateY(${Math.random() * 2}px)`
                      }}
                    >
                      {type.charAt(0).toUpperCase() + type.slice(1)} Button
                    </button>
                  ))}
                </div>

                <div 
                  className="p-4 rounded transition-all duration-300"
                  style={{ 
                    backgroundColor: themes[currentTheme].colors.secondary,
                    borderLeft: `4px solid ${themes[currentTheme].colors.primary}`
                  }}
                >
                  <p>
                    Current Theme: <strong>{themes[currentTheme].name}</strong><br />
                    Font Size: <strong>{fontSize}</strong><br />
                    Spacing Level: <strong>{spacing}</strong>
                  </p>
                </div>

                <div className="flex items-center gap-4">
                  <Palette className="w-6 h-6" style={{ color: themes[currentTheme].colors.accent }} />
                  <Sliders className="w-6 h-6" style={{ color: themes[currentTheme].colors.primary }} />
                  <Settings className="w-6 h-6" style={{ color: themes[currentTheme].colors.secondary }} />
                </div>
              </div>
            </InteractiveCard>
          </div>
        </main>

        <footer 
          className="mt-8 p-4 text-center"
          style={{ 
            backgroundColor: themes[currentTheme].colors.secondary,
            fontSize: fontSizes[fontSize].base
          }}
        >
          <p>Theme Switcher Demo - Customize your experience using the settings button in the header</p>
        </footer>
      </div>
    </ThemeContext.Provider>
  );
};

export default App;
